import React, { useState, useNavigate } from 'react'
import { Button, Form, } from 'react-bootstrap'

function SearchBox() {

    navigate = useNavigate()

    const [keyword, setKeyword] = useState('')

    const submitHandler = (e) => {
        e.preventDefault()
        if(keyword){
            navigate(`/?`)
        }
    }



  return (
    
    // <Form onSubmit={submitHandler}>
