import React, { useState, useEffect } from 'react'
import { Link, useParams, useNavigate } from 'react-router-dom'
import { Row, Col, Image, ListGroup, Button, Card, Form } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'

import Message from '../components/message'
import Loader from '../components/loader'
import FormContainer from '../components/formContainer'
import { getUserDetails, updateUser } from '../actions/userActions'
import { USER_UPDATE_RESET } from '../constants/userConstants'

function UserEditScreen() {


 
    const userId = useParams();
    const dispatch = useDispatch()
    const navigate = useNavigate()
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [isAdmin, setIsAdmin] = useState(false)

    const userDetails = useSelector(state => state.userDetails)
    const { error, loading, user } = userDetails
    
    const userUpdate = useSelector(state => state.userUpdate)
    const { error:errorUpdate, loading:loadingUpdate, success:successUpdate } = userUpdate

    useEffect(() => {

        if(successUpdate){
            dispatch({ type:USER_UPDATE_RESET})
            navigate('/admin/userlist')
            
        } else {
            if (!user.name || user._id !== Number(userId.id)) {
                dispatch(getUserDetails(userId.id))
            } else {
                setName(user.name)
                setEmail(user.email)
                setIsAdmin(user.isAdmin)
            }
        }

        
        
    }, [dispatch, navigate, user, userId, successUpdate])

    const submitHandler = (e) => {
        e.preventDefault() 
        dispatch(updateUser({ _id: user._id, name, email, isAdmin })) 
    }

    return (
        <div>
            <Link to='/admin/userlist'>Go Back</Link>
            <FormContainer>
            
                <h1>Edit User</h1>
                {loadingUpdate && <Loader />}
                {errorUpdate && <Message variant=''></Message>}

                {loadingUpdate ? <Loader /> : error ? <Message variant='danger'>{errorUpdate}</Message> : (

                    <Form onSubmit={submitHandler}>

                        <Form.Group controlId='name'>
                            <Form.Label>Name</Form.Label>
                            <Form.Control
                                type='name'
                                placeholder='Enter Name'
                                value={name}
                                onChange={(e) => setName(e.target.value)}    
                            >
                            </Form.Control>
                        </Form.Group>

                        <Form.Group controlId='email'>
                            <Form.Label>Email Address</Form.Label>
                            <Form.Control
                                type='email'
                                placeholder='Enter Email'
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}    
                            >
                            </Form.Control>
                        </Form.Group>

                        <Form.Group controlId='isadmin'>
                            <Form.Label>Password</Form.Label>
                            <Form.Check
                                type='checkbox'
                                label='Is Admin'
                                checked={isAdmin}
                                onChange={(e) => setIsAdmin(e.target.checked)}
                            >
                            </Form.Check>
                        </Form.Group>

                        <Button type='submit' variant='primary'>Update</Button>

                    </Form>
            
                )}    
            </FormContainer>    
        </div>
        
    ) 
}

export default UserEditScreen
