import React, { useState, useEffect} from 'react'
import { Link, useParams } from 'react-router-dom'
import { Row, Col, Image, ListGroup, Button, Card } from 'react-bootstrap'
import axios from 'axios'

import Rating from '../components/rating'
import products from '../products'


// function ProductScreen() {
    // const params = useParams();
    // const product = products.find((p) => p._id === params.id)

    // const [product, setProduct] = useState([])

    // useEffect(() => {

    //     async function fetchProduct() {
    //         const { data } = await axios.get(`/api/products/${product}`)
    //         setProduct(data)
    //     }

    //     fetchProduct()
        
    // }, [])

    function ProductScreen() {
        const { id } = useParams();
        const [product, setProduct] = useState({})
    
        useEffect(() => {
            async function fetchProduct() {
                const { data } = await axios.get(`/api/products/${id}`)
                setProduct(data)
            }
    
            fetchProduct()
        }, [id])



    return (
        
        <div>
            <h1>dqwd11d1d</h1>
            <Link to={`/`} className='btn btn-light my-3'>Go Back</Link>
            <Row>
                <Col md={6}>
                    <Image src={product.image} alt={product.name} fluid />
                </Col>
                <Col md={3}>
                    <ListGroup variant="flush">

                        <ListGroup.Item>
                            <h2>{product.name}</h2>
                        </ListGroup.Item>

                        <ListGroup.Item>
                            <Rating value={product.rating} text={<span style={{ margin: '0 25px' }}>{product.numReviews} reviews</span>} />
                        </ListGroup.Item>

                        <ListGroup.Item>
                            Price: ${product.price}
                        </ListGroup.Item>

                        <ListGroup.Item>
                            Description: {product.description}
                        </ListGroup.Item>

                    </ListGroup>
                </Col>
                <Col md={3}>
                    <Card>
                        <ListGroup variant="flush">
                            <ListGroup.Item>
                                <Row>
                                    <Col>Price:</Col>
                                    <Col>
                                        <strong>${product.price}</strong>
                                    </Col>
                                </Row>
                            </ListGroup.Item>
                            <ListGroup.Item>
                                <Row>
                                    <Col>Status:</Col>
                                    <Col>
                                        {product.countInStock > 0 ? "In stock" : "Out of stock"}
                                    </Col>
                                </Row>
                            </ListGroup.Item>
                            <ListGroup.Item>
                                <Row>
                                    <Button className='btn btn-block rounded' 
                                        disabled={product.countInStock === 0} type='button'>
                                        Add to Cart    
                                    </Button>
                                </Row>  
                            </ListGroup.Item>
                        </ListGroup>
                    </Card>
                </Col>
            </Row>
        </div>
)}

export default ProductScreen
